#!/bin/sh
sed -i \
         -e 's/rgb(0%,0%,0%)/#0a0000/g' \
         -e 's/rgb(100%,100%,100%)/#cccccc/g' \
    -e 's/rgb(50%,0%,0%)/#000000/g' \
     -e 's/rgb(0%,50%,0%)/#ff4c4c/g' \
 -e 's/rgb(0%,50.196078%,0%)/#ff4c4c/g' \
     -e 's/rgb(50%,0%,50%)/#110000/g' \
 -e 's/rgb(50.196078%,0%,50.196078%)/#110000/g' \
     -e 's/rgb(0%,0%,50%)/#c1c1c1/g' \
	"$@"
